(function(){"use strict";let forms=document.querySelectorAll('.php-email-form');forms.forEach(function(e){e.addEventListener('submit',function(event){event.preventDefault();let thisForm=this;let action=thisForm.getAttribute('action');let isNetlify=thisForm.hasAttribute('data-netlify');if(!action&&!isNetlify){displayError(thisForm,'The form action property is not set!');return}
thisForm.querySelector('.loading').classList.add('d-block');thisForm.querySelector('.error-message').classList.remove('d-block');thisForm.querySelector('.sent-message').classList.remove('d-block');let formData=new FormData(thisForm);if(isNetlify){submitToNetlify(thisForm,formData)}else{php_email_form_submit(thisForm,action,formData)}})});function submitToNetlify(thisForm,formData){fetch(thisForm.getAttribute('action'),{method:'POST',body:formData,headers:{'X-Requested-With':'XMLHttpRequest'}}).then(response=>{if(response.ok){return response.text()}else{throw new Error(`${response.status} ${response.statusText}`)}}).then(data=>{thisForm.querySelector('.loading').classList.remove('d-block');if(data.trim()==='Your message has been sent. Thank you!'){thisForm.querySelector('.sent-message').classList.add('d-block');thisForm.reset()}else{throw new Error(data?data:'Form submission failed and no error message returned.')}}).catch((error)=>{displayError(thisForm,error)})}
function displayError(thisForm,error){thisForm.querySelector('.loading').classList.remove('d-block');thisForm.querySelector('.error-message').innerHTML=error;thisForm.querySelector('.error-message').classList.add('d-block')}})()